import React, { Component } from 'react'
import './signup.css'

// import { Control, LocalForm, Errors } from 'react-redux-form';





class signup extends Component {

    constructor() {
        super();
        this.state = {
            fields: {},
            errors: {}
        }

        this.handleChange = this.handleChange.bind(this);
        this.submituserRegistrationForm = this.submituserRegistrationForm.bind(this);

    };

    handleChange(e) {
        let fields = this.state.fields;
        fields[e.target.name] = e.target.value;
        this.setState({
            fields
        });

        // if(this.validateForm()){

        // }
    }

    submituserRegistrationForm(e) {
        e.preventDefault();
        if (this.validateForm()) {
            alert('Current State is: ' + JSON.stringify(this.state));
            let fields = {};
            fields["username"] = "";
            fields["emailid"] = "";
            fields["mobileno"] = "";
            fields["password"] = "";
            fields["confirm_password"]="";
            this.setState({ fields: fields });
           
        }

    }

    validateForm() {

        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        if (!fields["username"]) {
            formIsValid = false;
            errors["username"] = "*Please enter your username.";
        }

        if (typeof fields["username"] !== "undefined") {
            if (!fields["username"].match(/^[a-zA-Z ]*$/)) {
                formIsValid = false;
                errors["username"] = "*Please enter alphabet characters only.";
            }
        }

        if (!fields["emailid"]) {
            formIsValid = false;
            errors["emailid"] = "*Please enter your email-ID.";
        }

        if (typeof fields["emailid"] !== "undefined") {
            //regular expression for email validation
            var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
            if (!pattern.test(fields["emailid"])) {
                formIsValid = false;
                errors["emailid"] = "*Please enter valid email-ID.";
            }
        }

        if (!fields["mobileno"]) {
            formIsValid = false;
            errors["mobileno"] = "*Please enter your mobile no.";
        }

        if (typeof fields["mobileno"] !== "undefined") {
            if (!fields["mobileno"].match(/^[0-9]{10}$/)) {
                formIsValid = false;
                errors["mobileno"] = "*Please enter valid mobile no.";
            }
        }

        if (!fields["password"]) {
            formIsValid = false;
            errors["password"] = "*Please enter your password.";
        }

        if (typeof fields["password"] !== "undefined") {
            if (!fields["password"].match(/^.*(?=.{8,})(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[@#$%&]).*$/)) {
                formIsValid = false;
                errors["password"] = "*Please enter secure and strong password.";
            }
        }

        if (!fields["confirm_password"]) {
            formIsValid = false;
            errors["confirm_password"] = "*Please enter your password.";
            
        }

        if (fields["confirm_password"]!==fields["password"]){
            formIsValid = false;
            errors["confirm_password"] = "Your password dosent match";

        }
        this.setState({
            errors: errors
        });
        return formIsValid;


    }


    render() {
        // const errors = this.validate(this.state.firstname, this.state.lastname, this.state.telnum, this.state.email);
        return (
            <div>
                <div class="bg-image"></div>

                <div class="bg-text">
                    <div className="row row-content">
                        <div className="col-12">
                            <h3>Welcome to the world of Books</h3>
                        </div>
                        <div id="register" >
                            <form method="post" name="userRegistrationForm" onSubmit={this.submituserRegistrationForm} >
                                <label className="form_label">Name</label>
                                <input type="text" name="username" value={this.state.fields.username}  onChange={this.handleChange} />
                                <div className="errorMsg">{this.state.errors.username}</div>
                                <label className="form_label">Email ID:</label>
                                <input type="text" name="emailid" value={this.state.fields.emailid} onChange={this.handleChange} />
                                <div className="errorMsg">{this.state.errors.emailid}</div>
                                <label className="form_label">Mobile No:</label>
                                <input type="text" name="mobileno" value={this.state.fields.mobileno} onChange={this.handleChange} />
                                <div className="errorMsg">{this.state.errors.mobileno}</div>
                                <label className="form_label">Password</label>
                                <input type="password" name="password" value={this.state.fields.password} onChange={this.handleChange} />
                                <div className="errorMsg">{this.state.errors.password}</div>
                                <label className="form_label">Confirm Password</label>
                                <input type="password" name="confirm_password" value={this.state.fields.confirm_password} onChange={this.handleChange} />
                                <div className="errorMsg">{this.state.errors.confirm_password}</div>
                                <input type="submit" className="button" value="Register" />
                            </form>
                        </div>
                    </div>

                </div>
            </div>
        );
    }

}


export default signup
