import React, { Component } from 'react'
import './signup.css'
import {
    Button, Form, FormGroup, FormFeedback, Input, Col, Label
} from 'reactstrap';
// import { Control, LocalForm, Errors } from 'react-redux-form';





class signup extends Component {

    constructor(props) {
        super(props);

        this.state = {
            firstname: '',
            lastname: '',
            telnum: '',
            email: '',
           password:'',
            touched: {
                firstname: false,
                lastname: false,
                telnum: false,
                email: false,
            }
        };

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleBlur = this.handleBlur.bind(this);

    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name]: value
        });
    }

    handleSubmit(event) {
        console.log('Current State is: ' + JSON.stringify(this.state));
        alert('Current State is: ' + JSON.stringify(this.state));
        event.preventDefault();
    }

    handleBlur = (field) => (evt) => {
        this.setState({
            touched: { ...this.state.touched, [field]: true }
        });
    }
    validate(firstname, lastname, email, telnum,password) {
        const errors = {
            firstname: '',
            lastname: '',
            telnum: '',
            email: '',
            password:''
        };
        const reg = /^\d$/;

        if (this.state.touched.firstname && firstname.length < 3) {
            errors.firstname = "First Name must be equal to or greter than 3"
        }
        else if (this.state.touched.firstname && firstname.length > 10) {
            errors.firstname = "First Name must less than 10 character"
        }
        else if (this.state.touched.firstname && reg.test(firstname)) {
            errors.firstname = "It should not contain numbers"
        }

        if (this.state.touched.lastname && lastname.length < 3) {
            errors.lastname = "First Name must be equal to or greter than 3"
        }
        else if (this.state.touched.lastname && lastname.length > 10) {
            errors.lastname = "First Name must less than 10 character"
        }

        if (this.state.touched.telnum&& !reg.test(telnum))
        errors.telnum="Contact Number must contain only number"


            return errors;
    }

    render() {
        const errors = this.validate(this.state.firstname, this.state.lastname, this.state.telnum, this.state.email);
        return (
            <div>
                <div class="bg-image"></div>

                <div class="bg-text">
                    <div className="row row-content">
                        <div className="col-12">
                            <h3>Send us your Feedback</h3>
                        </div>
                        <div className="col-12 col-md-9">
                            <Form onSubmit={this.handleSubmit}>
                                <FormGroup row>
                                    <Label htmlFor="firstname" md={2}>First Name</Label>
                                    <Col md={10}>
                                        <Input type="text" id="firstname" name="firstname"
                                            placeholder="First Name"
                                            value={this.state.firstname}
                                            valid={errors.firstname === ''}
                                            invalid={errors.firstname !== ''}
                                            onBlur={this.handleBlur('firstname')}
                                            onChange={this.handleInputChange} />
                                        <FormFeedback>{errors.firstname}</FormFeedback>
                                    </Col>
                                </FormGroup>
                                <FormGroup row>
                                    <Label htmlFor="lastname" md={2}>Last Name</Label>
                                    <Col md={10}>
                                        <Input type="text" id="lastname" name="lastname"
                                            placeholder="Last Name"
                                            value={this.state.lastname}
                                            valid={errors.lastname === ''}
                                            invalid={errors.lastname !== ''}
                                            onChange={this.handleInputChange} />
                                    </Col>
                                </FormGroup>
                                <FormGroup row>
                                    <Label htmlFor="telnum" md={2}>Contact Tel.</Label>
                                    <Col md={10}>
                                        <Input type="tel" id="telnum" name="telnum"
                                            placeholder="Tel. number"
                                            value={this.state.telnum}
                                            onChange={this.handleInputChange} />
                                    </Col>
                                </FormGroup>
                                <FormGroup row>
                                    <Label htmlFor="email" md={2}>Email</Label>
                                    <Col md={10}>
                                        <Input type="email" id="email" name="email"
                                            placeholder="Email"
                                            value={this.state.email}
                                            onChange={this.handleInputChange} />
                                    </Col>
                                </FormGroup>
                                <FormGroup row>

                                    
                                    <Label htmlFor="contactType" md={2}>Gender</Label>
                                    <Col md={{ size: 3, offset: 1 }}>
                                        <Input type="select" name="contactType"
                                            value={this.state.contactType}
                                            onChange={this.handleInputChange}>
                                            <option>Male</option>
                                            <option>Female</option>
                                        </Input>
                                    </Col>
                                </FormGroup>
                                <FormGroup row>
                                    <Label htmlFor="password" md={2}>Last Name</Label>
                                    <Col md={10}>
                                        <Input type="text" id="password" name="password"
                                            placeholder="Password"
                                            value={this.state.password}
                                            onChange={this.handleInputChange} />
                                    </Col>
                                </FormGroup>
                                <FormGroup row>
                                    <Col md={{ size: 10, offset: 2 }}>
                                        <Button type="submit" color="primary">
                                            signup
                                    </Button>
                                    </Col>
                                </FormGroup>
                            </Form>
                        </div>
                    </div>

                </div>
            </div>
        );
    }

}


export default signup
